INSERT INTO `wp_posts` VALUES (7,1,'2023-08-17 10:58:31','2023-08-17 10:58:31','<!-- wp:heading {\"style\":{\"typography\":{\"fontSize\":\"1.5em\"}}} -->\n<h2 class=\"wp-block-heading\" style=\"font-size:1.5em\">診療内容:</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>・皮膚診断、・眼科診断、・循環器診断、・外科診療、・避妊・去勢手術、・フィラリア・ノミ予防</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:image {\"id\":20,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image size-full is-style-rounded\"><img src=\"http://localhost:10018/wp-content/uploads/2023/08/clinic.jpg\" alt=\"\" class=\"wp-image-20\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"style\":{\"typography\":{\"fontSize\":\"1.5em\"}}} -->\n<h2 class=\"wp-block-heading\" style=\"font-size:1.5em\">診療対象:</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph -->\n<p>・ 午前 9:00～12:00</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>・ 午後 13:00～18:00</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>休診日: 水曜日・祝日</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>診察は予約制ではありませんが、皆様の待ち時間が短縮できるよう<br>ご来院前にお電話でご連絡をお願い致します。お時間のご案内を致<br>します。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>診療専用ダイヤル: 012-345-5431</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>※ 救急の場合には 休診日や診察時間外に関わらず、可能な限り対<br>応いたします、お電話でご相談ください。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>※ クレジットカード及び動物保険対応</p>\n<!-- /wp:paragraph -->','診療案内','','publish','closed','closed','','guide','','','2023-08-20 08:34:00','2023-08-20 08:34:00','',0,'http://localhost:10018/?page_id=7',2,'page','',0);
