INSERT INTO `wp_posts` VALUES (11,1,'2023-08-17 10:59:13','2023-08-17 10:59:13','<!-- wp:paragraph -->\n<p>・所在地： 〒270-0123 千葉県松戸市向ヶ丘2-3-45 柴田レジデンス2階<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3236.39840362003!2d139.9209728404226!3d35.79015148471829!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x6018848eb61b1573%3A0x82ebe827e56d0fa3!2z44CSMjcwLTIyNDIg5Y2D6JGJ55yM5p2-5oi45biC5Luy5LqV55S677yR5LiB55uu77yU4oiS77yRIOS6rOiRiemKgOihjOadvuaIuOaWsOeUsOaUr-W6lw!5e0!3m2!1sja!2sjp!4v1692540227747!5m2!1sja!2sjp\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>・車でお越しの方<br>新京成線 みのり台近く、当院の前に駐車場有り。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>・電車でお越しの方<br>最寄り駅 新京成線 みのり台</p>\n<!-- /wp:paragraph -->','アクセス','','publish','closed','closed','','access','','','2023-08-20 14:04:13','2023-08-20 14:04:13','',0,'http://localhost:10018/?page_id=11',4,'page','',0);
